mapper.select:
select h.* from o2_home h where h.num not in ( select distinct h_num from o2_home_guest where
( checkin between to_date(#{checkin}) and to_date(#{checkout})-1 or checkout between to_date(#{checkin})+1 and to_date(#{checkout}) )
) and addr LIKE '%'||#{addr}||'%'
AND person >= #{person}

SELECT * FROM ( SELECT A.*, ROWNUM AS RNUM FROM ( select h.* from o2_home h where 1=1 and h.num not in ( select distinct h_num from o2_home_guest where ( checkin between to_date(#{checkin}) and to_date(#{checkout})-1 or checkout between to_date(#{checkin})+1 and to_date(#{checkout}) ) ) and addr LIKE '%'||#{addr}||'%'
AND person >= #{person}
AND REGEXP_LIKE (tag, #{tag})
AND price BETWEEN #{price_from} AND #{price_to}
ORDER BY price ) A
WHERE ROWNUM <= 3 ) WHERE RNUM > 0
